@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-white text-gray-900;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
    line-height: 1.6;
  }

  /* Custom CSS Variables for Colors */
  :root {
    --primary: #00BFA6;
    --coral: #FF6F61;
    --royal-blue: #4169E1;
    --bright-yellow: #FFD700;
    --light-gray: #F8F9FA;
    --medium-gray: #E9ECEF;
    --dark-gray: #6C757D;
    --text-primary: #212529;
    --text-secondary: #6C757D;
  }

  /* Typography */
  h1, h2, h3, h4, h5, h6 {
    font-weight: 700;
    line-height: 1.2;
    color: var(--text-primary);
  }

  h1 { font-size: 3.5rem; }
  h2 { font-size: 2.5rem; }
  h3 { font-size: 2rem; }
  h4 { font-size: 1.5rem; }

  @media (max-width: 768px) {
    h1 { font-size: 2.5rem; }
    h2 { font-size: 2rem; }
    h3 { font-size: 1.5rem; }
  }

  /* Section Styles */
  section {
    @apply py-20;
    position: relative;
  }

  .section-light {
    background: linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%);
  }

  .section-gray {
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  }

  .section-primary {
    background: linear-gradient(135deg, #00BFA6 0%, #00A693 100%);
    color: white;
  }

  .section-primary h2,
  .section-primary h3,
  .section-primary h4 {
    color: white;
  }

  /* Cards */
  .modern-card {
    @apply bg-white rounded-2xl shadow-lg border border-gray-100;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    backdrop-filter: blur(10px);
  }

  .modern-card:hover {
    @apply shadow-2xl;
    transform: translateY(-8px);
    border-color: var(--primary);
  }

  /* Buttons */
  .btn-primary {
    background: linear-gradient(135deg, var(--primary) 0%, #00A693 100%);
    @apply text-white font-semibold py-4 px-8 rounded-xl;
    transition: all 0.3s ease;
    border: none;
    cursor: pointer;
    position: relative;
    overflow: hidden;
  }

  .btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(0, 191, 166, 0.3);
  }

  .btn-primary::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
    transition: left 0.5s ease;
  }

  .btn-primary:hover::before {
    left: 100%;
  }

  .btn-secondary {
    background: linear-gradient(135deg, var(--coral) 0%, #FF5A4A 100%);
    @apply text-white font-semibold py-4 px-8 rounded-xl;
    transition: all 0.3s ease;
    border: none;
    cursor: pointer;
  }

  .btn-secondary:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(255, 111, 97, 0.3);
  }

  .btn-outline {
    @apply border-2 border-gray-300 text-gray-700 font-semibold py-4 px-8 rounded-xl bg-transparent;
    transition: all 0.3s ease;
    cursor: pointer;
  }

  .btn-outline:hover {
    @apply border-gray-900 text-gray-900;
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
  }

  /* Text Gradients */
  .text-gradient-primary {
    background: linear-gradient(135deg, var(--primary) 0%, var(--royal-blue) 100%);
    @apply bg-clip-text text-transparent;
  }

  .text-gradient-coral {
    background: linear-gradient(135deg, var(--coral) 0%, var(--bright-yellow) 100%);
    @apply bg-clip-text text-transparent;
  }

  /* Animations */
  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes fadeInLeft {
    from {
      opacity: 0;
      transform: translateX(-30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes fadeInRight {
    from {
      opacity: 0;
      transform: translateX(30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInDown {
    from {
      opacity: 0;
      transform: translateY(-30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-fade-in-up {
    animation: fadeInUp 0.8s ease forwards;
  }

  .animate-fade-in-left {
    animation: fadeInLeft 0.8s ease forwards;
  }

  .animate-fade-in-right {
    animation: fadeInRight 0.8s ease forwards;
  }

  .animate-slide-in-down {
    animation: slideInDown 0.8s ease forwards;
  }

  /* Progress Bar */
  .progress-bar {
    @apply bg-gray-200 rounded-full h-3 overflow-hidden;
    position: relative;
  }

  .progress-fill {
    @apply h-full rounded-full;
    transition: width 1.5s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
  }

  .progress-fill::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
    animation: shimmer 2s infinite;
  }

  @keyframes shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
  }

  /* Section Dividers */
  .section-divider {
    width: 100px;
    height: 4px;
    background: linear-gradient(90deg, var(--primary) 0%, var(--coral) 50%, var(--royal-blue) 100%);
    @apply mx-auto my-8 rounded-full;
  }

  /* Hero Section */
  .hero-title {
    font-size: 4rem;
    font-weight: 800;
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  @media (max-width: 768px) {
    .hero-title {
      font-size: 2.5rem;
    }
  }

  .hero-subtitle {
    font-size: 1.5rem;
    font-weight: 400;
    color: var(--text-secondary);
    margin-bottom: 2rem;
  }

  .hero-description {
    font-size: 1.125rem;
    line-height: 1.7;
    color: var(--text-secondary);
    margin-bottom: 3rem;
  }

  /* Contact Form */
  .form-input {
    @apply w-full px-4 py-4 border-2 border-gray-200 rounded-xl;
    transition: all 0.3s ease;
    font-size: 1rem;
  }

  .form-input:focus {
    @apply border-blue-500 outline-none;
    box-shadow: 0 0 0 3px rgba(65, 105, 225, 0.1);
  }

  .form-textarea {
    @apply w-full px-4 py-4 border-2 border-gray-200 rounded-xl resize-none;
    transition: all 0.3s ease;
    font-size: 1rem;
    min-height: 120px;
  }

  .form-textarea:focus {
    @apply border-blue-500 outline-none;
    box-shadow: 0 0 0 3px rgba(65, 105, 225, 0.1);
  }

  /* Social Icons */
  .social-icon {
    @apply w-12 h-12 rounded-full flex items-center justify-center text-white;
    transition: all 0.3s ease;
    cursor: pointer;
  }

  .social-icon:hover {
    transform: translateY(-3px) scale(1.1);
  }

  .social-linkedin {
    background: linear-gradient(135deg, #0077B5 0%, #005885 100%);
  }

  .social-github {
    background: linear-gradient(135deg, #333 0%, #000 100%);
  }

  .social-email {
    background: linear-gradient(135deg, var(--coral) 0%, #FF5A4A 100%);
  }

  /* Responsive Grid */
  .responsive-grid {
    display: grid;
    gap: 2rem;
  }

  .responsive-grid.cols-1 {
    grid-template-columns: 1fr;
  }

  .responsive-grid.cols-2 {
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  }

  .responsive-grid.cols-3 {
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  }

  /* Utility Classes */
  .container-custom {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1.5rem;
  }

  .text-balance {
    text-wrap: balance;
  }

  .border-gradient {
    position: relative;
    background: white;
    border-radius: 1rem;
    padding: 1px;
  }

  .border-gradient::before {
    content: '';
    position: absolute;
    inset: 0;
